var mongo = require('mongodb');
var ObjectId = mongo.ObjectId;
var mongoSanitize = require('express-mongo-sanitize');

module.exports = function(app){
	return {
		checkIn: function(json, fields, callback){
			fields.forEach(function(elem){
				if(!(elem in json)){
					callback(elem, false);
				} else{
					callback(elem, true);
				}
			});
		}
	}
}